sp_widget.client_script:
api.controller=function(spModal) {
  /* widget controller */
  var c = this;
		c.onAlert = function (name,color){
		spModal.open({
		widget: 'cap_popup',
		size: 'popupFullScreen',
		widgetInput:{
				name:name,
				color: color
			},
		headerStyle: {display: "none"},
		footerStyle: {display: "none"},
	});
	};
	c.learngPath = function(name,color){
		spModal.open({
			widget: 'cap_learning_path',
// 			size: 'CaplearningPath',
			size: 'popupFullScreen',
			widgetInput:{
				name: name,
				color: color
			},
			headerStyle: {display: "none"},
			footerStyle: {display: "none"},

		});
	}
}

