sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var colorList = ['blue', 'violet', 'white', 'orange', 'green', 'emerald'];
	data.capability = [];

	var grCap = new GlideRecord('cmdb_ci_business_capability');
	grCap.addQuery('x_292088_capabil_0_cap_lead', '!=', 'NULL');
	grCap.addQuery('hierarchy_level', '1');
	grCap.query();

	var count = 0;

	while (grCap.next()){
		var capData = {};
		capData.name = grCap.name.toString();
		capData.lead = grCap.x_292088_capabil_0_cap_lead.user_name.toString();

		if (count % 2 === 0){
			capData.sideClass = 'info-right';
		} else {
			capData.sideClass = 'info-left';
		}

		capData.colorClass = colorList[count % colorList.length];

		data.capability.push(capData);
		count += 1;
	}

})();
