sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.mode = $sp.getParameter('mode');
	data.capSysId = $sp.getParameter('cap_id');
	data.featureWidget = $sp.getWidget('avenger_navlink');

	if (!input) {
    var grDBImage = new GlideRecord('db_image');
    grDBImage.addEncodedQuery("nameSTARTSWITHtest^name!=test_nho.png^ORname=NULL");
    grDBImage.orderBy('sys_name');
    grDBImage.query();

    var imageNames = [];
    while (grDBImage.next()) {
      var imageName = grDBImage.getValue('sys_name');
      imageNames.push(imageName);
    }

    var renderedImages = {};
    for (var i = 0; i < imageNames.length; i++) {
      var imageName = imageNames[i];
      renderedImages["image" + (i + 1)] = imageName;
    }

    data.renderedImages = renderedImages;
  }


})();
