sp_widget.client_script:
api.controller = function ($scope,$rootScope, $location) {
	/* Widget Controller */
	c = this;
	$scope.flag = false;
	$scope.count = 0;
	c.switchMode = function(mode){
		var parms = {
			id: 'capability',
			mode: mode
		};
		$location.search(parms);

	$('header').hide();
	$('footer').hide();
	}


};

