sp_widget.script:
(function () {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.mode = $sp.getParameter('mode');
	if (!input) {
		//replace the below userID with the user you want to display in the image of this intro section
		var renderedImages = {
			"image1":"TVU59",
			"image2":"QTRAN43",
			"image3":gs.getProperty('x_292088_capabil_0.image4.introduction.section.capability.page', ''),
			"image4":"HTRAN210",
			"image5":"NDANG3",
			"image6":gs.getProperty('x_292088_capabil_0.image6.introduction.section.capability.page', '')
		};

		var grUser = new GlideRecord('sys_user');
		grUser.addEncodedQuery("user_name="+ renderedImages.image1 + 
													 "^ORuser_name=" + renderedImages.image2 +
													 "^ORuser_name=" + renderedImages.image4 +
													 "^ORuser_name=" + renderedImages.image5);
		grUser.query();

		var avtObject = {};

		while (grUser.next()) {
			avtObject[grUser.user_name] = "sys_attachment.do?sys_id=" + grUser.photo;
		}

		for (var attribute in renderedImages) {
			if (avtObject.hasOwnProperty(renderedImages[attribute])) {
				renderedImages[attribute] = 'background-image: url(' + avtObject[renderedImages[attribute]] + ')';
			}
			else{
				renderedImages[attribute] = 'background-image: url(' + renderedImages[attribute]+ ')';
			}

		}

		data.renderedImages = renderedImages;
	}
})();
