sp_widget.client_script:
function($rootScope, $scope, $location) {
	/* widget controller */
	var c = this;

	$scope.onClick = function(event) {
		var id = event.target.id.toString();
		var parms;
		if(id == 'introduction') {
			parms = {
				id: 'capability',
				mode: 'avenger'
			};
		} else {
			parms = {
				id: 'capability',
				mode: 'avenger',
				cap_id: event.target.id.toString()
			};
		}
		$location.search(parms);
	};

}

