sp_widget.script:
(function () {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.mode = $sp.getParameter('mode');
	data.capabilities = [];

	var capGR = new GlideRecord("cmdb_ci_business_capability");
	capGR.addEncodedQuery(
		"parent=e31711779757cd507fd7f97e6253af80^hierarchy_level=1^active=true^x_292088_capabil_0_cap_lead!=NULL"
	);
	capGR.orderBy('order');
	capGR.query();

	while (capGR.next()) {
		var cap_obj = {};
		cap_obj.sys_id = capGR.getUniqueValue();
		cap_obj.name = capGR.getValue("name");
		cap_obj.image =
			"/" +
			capGR.getValue("u_background_hero") +
			".iix";
		data.capabilities.push(cap_obj);
	}
})();
