sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

    if (isLoading || newValue === '') {
        return;
    }

    var hasError = false;
    var maxNumberOfSubLeads = 3;
    var capLeadValue = g_form.getValue("x_292088_capabil_0_cap_lead");
    var subLeadItems = g_form.getValue("x_292088_capabil_0_sub_leads").split(',');

    if (subLeadItems.length > maxNumberOfSubLeads) {
        g_form.showFieldMsg("x_292088_capabil_0_sub_leads", "The of sub leads must be smaller than 3!", "error");
        hasError = true;
    }

    if (subLeadItems[subLeadItems.length - 1].toString() == capLeadValue) {
        g_form.showFieldMsg("x_292088_capabil_0_sub_leads", "Sub lead and Cap lead cannot be the same!", "error");
        hasError = true;
    }

    if (hasError == true) {
        //remove the sub lead has just been added to the list which cause the error
        setTimeout(removeSubLead, 2000);

        function removeSubLead() {
            subLeadItems.splice(-1);
            g_form.setValue("x_292088_capabil_0_sub_leads", subLeadItems.toString());;
        }
    }

}
