sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var capLeadValue = g_form.getValue("x_292088_capabil_0_cap_lead");
    var subLeadItems = g_form.getValue("x_292088_capabil_0_sub_leads").split(',');

    for (var i = 0; i < subLeadItems.length; i++) {
        if (subLeadItems[i].toString() == capLeadValue) {
			g_form.clearValue("x_292088_capabil_0_cap_lead");
            g_form.showFieldMsg("x_292088_capabil_0_cap_lead", "Cap lead and Sub lead cannot be the same!", "error");
        }
    }
}
