sys_script_include.script:
var CapabilityUtils = Class.create();
CapabilityUtils.prototype = {
    initialize: function() {
    },

	getGeneralCapInfo: function() {
		var data = [];
		var colorList = gs.getProperty('x_292088_capabil_0.dxc.capability.color_set').split(',');
		var count = 0;

		var grCap = new GlideRecord('cmdb_ci_business_capability');
		grCap.addQuery('x_292088_capabil_0_cap_lead', '!=', 'NULL');
		grCap.addQuery('hierarchy_level', '1');
		grCap.addActiveQuery();
		grCap.orderBy('order');
		grCap.query();
		while (grCap.next()){
			var capData = {};

			capData.name = grCap.u_full_name.toString();
			//Get Cap lead name = First name + Sure name
			var capLeadFullName = grCap.x_292088_capabil_0_cap_lead.name.toString().split(' ');
			capData.lead = capLeadFullName.slice(0, 2).join(' ');

			capData.avatar = grCap.x_292088_capabil_0_cap_lead.avatar.getDisplayValue();
			if (!capData.avatar){//If the current cap lead does not have an avatar then display the default snow user avatar
				capData.avatar = gs.getProperty('x_292088_capabil_0.dxc.capability.default-user-avatar');
			}

			if (count % 2 === 0){
				capData.sideClass = 'info-right';
			} else {
				capData.sideClass = 'info-left';
			}

			capData.colorClass = colorList[count % colorList.length].trim();

			data.push(capData);
			count += 1;
		}

		return data;
	},

    type: 'CapabilityUtils'
};
